'use strict';

let app;

module.exports.require = function(path, fractal){
    if (fractal) {
        app = fractal;
    }
    if (!app) {
        throw new Error('You must pass a Fractal instance to .use() before requiring helpers.');
    }
    return require(`./${path}`)(app);
};

module.exports.use = function(fractal){
    app = fractal;
};
